            //Huaje1, lengua zapoteco8,
            ['ced_autor'=>'1','ced_lengua'=>'8', 'ced_url'=>'1', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'1',   'ced_audio'=>'',                       'ced_codigo'=>'GUICHIDIIDXA’LU DIDXAZÁ'],
