            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'1',  'ced_audio'=>'',                         'ced_codigo'=>'Kìví itún yó’o nùú na ñuu'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'2',  'ced_audio'=>'sabino_tuunsavi_01.ogg',   'ced_codigo'=>'Tùíkun'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'3',  'ced_audio'=>'',                         'ced_codigo'=>'Kìví itún yó’o nùú na ká’vi'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'4',  'ced_audio'=>'sabino_tuunsavi_02.ogg',   'ced_codigo'=>'<i>Taxodium mucronatum</i> Ten.'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'5',  'ced_audio'=>'',                         'ced_codigo'=>'Tá’án itún iin kátyi kûnì koo'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'6',  'ced_audio'=>'sabino_tuunsavi_03.ogg',   'ced_codigo'=>'CUPRESSACEAE'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'7',  'ced_audio'=>'',                         'ced_codigo'=>'Nùú íyo itún yó’o'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'8',  'ced_audio'=>'sabino_tuunsavi_04.ogg',   'ced_codigo'=>'íyo nú Ñuu Kǒ’yo Ká’nu xí’ín Ñuu Guatemala.'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'9',  'ced_audio'=>'',                         'ced_codigo'=>'Nùú kútóo itún yó’o koo nú'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'10', 'ced_audio'=>'sabino_tuunsavi_05.ogg',   'ced_codigo'=>'kútoo nú koo nú nùú ndíkàà tìkuǐi, yu’ú ndò’yo, yu’ú ìtia xí’ín yu’ú ìvì, íyo tu nú tiañu ikú nùú ndàà.'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'1','ced_order'=>'11', 'ced_audio'=>'',                         'ced_codigo'=>'Ná ndatú’ún lo’o yu xá’á itún yó’ó xí’ín ndó'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'12', 'ced_audio'=>'sabino_tuunsavi_06.ogg',   'ced_codigo'=>'Ndítyi iin tá’án nú itún yó’o, iin nú ká’nu và’a, ñuu naní Santa María Tule, ñuu nà tá’án yó ndátú’ún zapoteco, kù’va sava hora kee yó Ñuu Ndǔvá yó’o ra xàà yó ikán. Xà na’á kuìyà kúú ña ndítyi nú ñuu nà kán, kù’va ùvì míìl kuìyà káá kúú ña. Nà ká’vi, nà ká’àn nà xí’ín arqueólogos, kátyi nà saá, sana níi ra, xà nìxìkòo ñuu Tule yó’o kuìyà saá ví tyè, ta na ñuu yó’o kúú na xìki’in kuénda xí’ín tùíkun yó’o ndítyi ndiàà vityin. Sana ra mií tùíkun yó’o kúú yàà va nú nìxìyo Ñuu Ndǔvá yó’o, ndiàà jardín yó’o va nìxìyo nú. Ta, tá’án itún yó’o kúú nú kùva’à escultura xí’ín tiàyù kuálí ndíta ini jardín yó’o kuni ndó.'],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'13', 'ced_audio'=>'sabino_tuunsavi_07.ogg',   'ced_codigo'=>'Ká’àn nà ká’vi và’a xà’á Ñuu Ndǔvá, ñà nèkée kùu kàna tùíkun yó’o Ñuu Tule, nà xìndee xìna’á kúú na ndátú’ún tyè, rà táta naní Quetzalcóatl kúú ra tyì’i tùíkun yó’o. Atú sǔvi ra yó’o kúú ra, rà táta naní Pecocha, rà kèe Ñuu Nicaragua, rà kán kúú tuku ra tyì’i nú tyè. Inka tuku na ndátú’ún, nà xìndee xìna’á Ñuu Tule yó’o tyè xìxa’ndià níi nà itún, xìxà’ní níi nà kití ikú, kòó níki’in na kù’va ra yàà ra nì ìtyì va ikú ñuu nà, nì ìtyì tìkuǐi nà, kìxàá ndó’o ini nà nùú ñà’a. Saá xíka iin ra táta naní Condoy, rà tyée ínuu nùú na ñuu ayuuk mixe, xíka ra nìxàà rà Ñuu Tule yó’o, ta tée xìni nà rà nìkà’àn nà xí’ín ra, xàku ndá’vi nà nùú ra, ña’á kúú ñá, tiàa kúú ra, kití kúú rí, xàku ndá’vi ndi’i na nùú ra ná tyindieé tá’án ra xí’ín na. Ta saá, xìni rà ndá’vi ví ndó’o na, nándikó ini nà xà’ndia na itún, xà’ní na kití ta kùndǎ’vi ini rà xìni rà nà. Kì’in ra karróti ní’i ra ta tùvi ra nú nùú ñu’ú saá vityin vityin ndàkaka vìkó nùú ndiví ta kìxàá sàví kúún rá, kùun kùun rá ta saá yàà ndùkuîí ikú ñuu nà. Karróti yó’o kúú nú nàna ndíxín ìkù nda’á, ndùkǒ’yo xà’á ta ndùú nú tùíkun. Ta saá ndàkoo ra karróti rà ndítyi nú ñuu ikán tyi xà tùíkun va ndùu nú, ñà kán ná kundàà ini nà rà yó’o kúú ra kàndityi nú. '],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'14', 'ced_audio'=>'sabino_tuunsavi_08.ogg',   'ced_codigo'=>'Ñà yó’o kúú ña ndátú’ún na tá’án yó xà’á tùíkun yó’o kuìyà xìndikàà áta Porfirio Diaz ve’e tyíñu Ñuu Kǒ’yo. Su xà yàtyì kà ví íyo itún yó’o tyi xà ndàña’à nà nú ínuu nú nùú tutu naní códice <b>Yuta Tnoho</b> (án ña xìka’àn nà xí’ín Códice Vindobonense). Tutu yó’o kèe ña iin ñuu naní Ñuu Savi Ñu’u ndíkùn ndiaa Ñuu Ndǔvá, ta ndátú’ún ña xí’ín yó nìsaa kùu nìxìkòo ǐvi nùú ndéé yó yó’o nùúna ñuu ísàví xìndee xìna’á. Nùú tutu yó’o ndàña’à nà iin tùíkun yó’o ta tokó nú yó’o kàku ùvì nà ìví, iin ña’á iin tiàa. Nà yó’o kúú na nùú nùú kuití nìtùvi nùú ta sakuǐnà na ñuu ndéé ]yó vityin. '],
            ['ced_autor'=>'1','ced_lengua'=>'6', 'ced_url'=>'2', 'ced_jardin'=>'1','ced_titulo'=>'0','ced_order'=>'15', 'ced_audio'=>'',                       'ced_codigo'=>"<br><br><br><small>Traducción y voz tu'un savi: Juana Mendoza</small>"],
